#include <vector>

using namespace std;
using namespace cv;
using namespace som;

namespace som {
    static const int COLORMAP_COLORS = 256;
    
    extern vector<uchar> getPseudocolorScheme(const ColorScale scale);
}

Mat som::getColormap(const ColormapConfiguration colormapConfiguration) {
    float cmin = colormapConfiguration.cmin;
    float cmax = colormapConfiguration.cmax;
    size_t colors = colormapConfiguration.colors;
    
    assert(colors > 1 && colors <= COLORMAP_COLORS);
    assert((cmin >= 0 && cmin < 1) && (cmax > 0 && cmax <= 1.0));
    assert(cmin < cmax);
    
    vector<uchar> scheme = getPseudocolorScheme(colormapConfiguration.colorscale);
    
    Mat colormap(COLORMAP_COLORS, 1, CV_8UC3);
    auto channels = colormap.channels();
    
    for (auto i = 0; i < COLORMAP_COLORS; i++) {
        auto index0 = i * channels;
        
        for (auto j = 0; j < channels; j++) {
            colormap.at<Vec3b>(i, 0)[j] = scheme[index0 + j];
        }
    }
    
    if (colormapConfiguration.invert) {
        flip(colormap, colormap, 0);
    }
    
    Mat recoloredMap;
    if (colors < COLORMAP_COLORS) {
        auto step = (float)COLORMAP_COLORS / colors;
        
        resize(colormap, recoloredMap, Size(1, colors));
        
        for (auto i = 0; i < colors; i++) {
            auto segment = i * step;
            line(colormap, Point2f(0, segment), Point2f(0, segment + step), recoloredMap.at<Vec3b>(i));
        }
    }
    
    if (cmin > 0 || cmax < 1.0) {
        auto diff = cmax - cmin;
        auto middleSegment = diff * COLORMAP_COLORS;
        auto minSegment = cmin * COLORMAP_COLORS;
        
        if (recoloredMap.empty()) {
            recoloredMap = colormap.clone();
        }
        
        line(colormap, Point2f(0, 0), Point2f(0, minSegment), recoloredMap.at<Vec3b>(0, 0));
        
        auto step = middleSegment / colors;
        for (auto i = 0; i < colors; i++) {
            auto segment = minSegment + (i * step);
            line(colormap, Point2f(0, segment), Point2f(0, segment + step), recoloredMap.at<Vec3b>(i));
        }
        
        line(colormap, Point2f(0, minSegment + middleSegment), Point2f(0, COLORMAP_COLORS - 1), recoloredMap.at<Vec3b>(0, recoloredMap.rows - 1));
    }
    
    return colormap;
}

